import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogames} from '../actions';
import InfiniteScroll from 'react-infinite-scroll-component';
import './Home.css'
//import { Link } from 'react-router-dom';
import Card from './Card';

export default function Home(){
    const dispatch = useDispatch();
    const allVideogames = useSelector((state) => state.allVideogames);
    const [page, setPage] = useState(1);

    useEffect(() => {
        dispatch(getVideogames());
    }, [dispatch]);

    function handleClick(e){
        e.preventDefault();
        dispatch(getVideogames());
    }

    return (
        <div className='home-container'>
            <h1>HOME</h1>
            <button onClick={e =>{ handleClick(e)}}>Reset</button>
            <InfiniteScroll dataLength={allVideogames.length} hasMore={true} next={() => setPage(prevPage => prevPage + 1)}>
            <div className='home-cards'>
            { 
                allVideogames && allVideogames.map(el =>{
                    return(
                    <Card
                        key={el.id}
                        name={el.name}
                        genres={el.createdInDb?el.genres.map(g => g.name + " ") : el.genres.map(g => g + " ")}
                        image={el.image}
                    />
                    )
                })          

            }
            </div>
            </InfiniteScroll>
        </div>
    )
}
