import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getVideogames, getMoreVideogames} from '../actions';
import InfiniteScroll from 'react-infinite-scroll-component';
import './Home.css'
//import { Link } from 'react-router-dom';
import Card from './Card';

export default function Home(){
    const dispatch = useDispatch();
    const allVideogames = useSelector((state) => state.allVideogames);
    //PAGINATION
    const [currentPage, setCurrentPage] = useState(1);
    const [videogamesPerPage, setVideogamesPerPage] = useState(15);
    const indexOfLastVideogame = currentPage * videogamesPerPage;
    const indexOfFirstVideogame = indexOfLastVideogame - videogamesPerPage;
    const currentVideogames = allVideogames.slice(indexOfFirstVideogame, indexOfLastVideogame)

    
    useEffect(() => {
        dispatch(getVideogames());
    }, []);

    function handleClick(e){
        e.preventDefault();
        dispatch(getVideogames());
    }
    
    function handleMore(e){
        //e.preventDefault();
        dispatch(getMoreVideogames());
    }
    console.log('SEE GAMES', allVideogames)
    
    return (
        <div className='home-container'>
            <h1>HOME</h1>
            <button onClick={e =>{ handleClick(e)}}>RESET</button>
            
            <div className='home-cards'>
            {   
                allVideogames.length > 0 &&
                allVideogames.map(el =>{
                    return(
                    
                    <Card
                        key={el.id}
                        name={el.name}
                        genres={el.createdInDb?el.genres.map(g => g.name + " ") : el.genres.map(g => g + " ")}
                        image={el.image}
                    />
                    
                    )
                })          

            }
            </div>
            <button onClick={ e => {handleMore(e)}}>MORE</button>
            
        </div>
    )
}
