const initialState = {
    allVideogames: [],
}



function rootReducer(state = initialState, action) { 
    switch(action.type){
        case "GET_VIDEOGAMES":
            return{
                ...state,
                allVideogames: action.payload
            }
    }
}

export default rootReducer;
